import { useState, useEffect } from 'react'
import axios from "axios";
import {Link, useNavigate} from "react-router-dom";

const OrderList = () => {
    const [description, setDescription] = useState('');
    const [status, setStatus] = useState('');
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        getOrders();
    }, []);

    const getOrders = async () => {
        const response = await axios.get('http://localhost:5000/orders');
        setOrders(response.data);
    }

    const deleteOrder = async (id) => {
        await axios.delete(`http://localhost:5000/orders/${id}`);
        getOrders();
    }

    const addOrder = async () => {
        setDescription(orders.slice(-1)[0].description);
        setStatus(orders.slice(-1)[0].status);
        await axios.post('http://localhost:5000/orders',{
            description: description,
            status: status
        });
        getOrders();
    }

    return (
        <div>
            <button onClick={ () => addOrder() } className="button is-small is-danger">Add order</button>
            <table className="table is-striped is-fullwidth">
                <thead>
                <tr>
                    <th>Nr</th>
                    <th>Description</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                { orders.map((order) => (
                    <tr key={ order.id }>
                        <td>{ order.id }</td>
                        <td>{ order.description }</td>
                        <td>{ order.status }</td>
                        <td>
                            <Link to={`/edit/${order.id}`} className="button is-small is-info">Edit</Link>
                            <button onClick={ () => deleteOrder('open') } className="button is-small is-danger">Open</button>
                            <button onClick={ () => deleteOrder('preparing') } className="button is-small is-danger">Preparing</button>
                            <button onClick={ () => deleteOrder('served') } className="button is-small is-danger">Served</button>
                            <button onClick={ () => deleteOrder(order.id) } className="button is-small is-danger">Delete</button>
                        </td>
                    </tr>
                )) }

                </tbody>
            </table>
        </div>
    )
}

export default OrderList
