import { useState, useEffect } from 'react'
import axios from "axios";
import {Link} from "react-router-dom";
import Pagination from './components/Pagination';

const OrderList = () => {
    const [orders, setOrders] = useState([]);
    const [checked, setChecked] = useState([]);

    // To hold the actual data
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(true);

    const [currentPage, setCurrentPage] = useState(1);
    const [recordsPerPage] = useState(10);


    useEffect(() => {
        axios.get('MOCK_DATA.json')
            .then(res => {
                setData(res.data);
                setLoading(false);
            })
            .catch(() => {
                alert('There was an error while retrieving the data')
            })
    }, [])

    const indexOfLastRecord = currentPage * recordsPerPage;
    const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
    const currentRecords = data.slice(indexOfFirstRecord, indexOfLastRecord);
    const nPages = Math.ceil(data.length / recordsPerPage)
    useEffect(() => {
        getOrders();
    }, []);

    const addOrder = async () => {
        const foodArray = ['pizza', 'pasta', 'rice', 'lasagne', 'soup', 'smurgeonisauce', 'chicken', 'stamppot'];
        let item = foodArray[Math.floor(Math.random() * foodArray.length)];

        await axios.post('http://localhost:3001/orders', {
            description: item,
            status: 'open'
        });
        getOrders();
    }

    const changeStatus = async (id, status) => {
        await axios.patch(`http://localhost:3001/orders/${id}`, {
            status: status
        });
        getOrders();
    }

    const changeStatusses = (checked, status) => {
        checked.forEach((item) => {
            changeStatus(item, status);
        });
        getOrders();
    };

    const deleteOrder = async (id) => {
        await axios.delete(`http://localhost:3001/orders/${id}`);
        getOrders();
    }

    const deleteOrders = (checked) => {
        checked.forEach((item) => {
            deleteOrder(item);
        });
        getOrders();
    };

    const getOrders = async () => {
        const response = await axios.get('http://localhost:3001/orders');
        setOrders(response.data);
    }

    const handleCheck = (event) => {
        let updatedList = [...checked];
        if (event.target.checked) {
            updatedList = [...checked, event.target.value];
        } else {
            updatedList.splice(checked.indexOf(event.target.value), 1);
        }
        setChecked(updatedList);
    };

    return (
        <div>
            <button onClick={ () => addOrder() } className="button is-small is-danger">Add order</button>
            <table className="table is-striped is-fullwidth">
                <thead>
                <tr>
                    <th />
                    <th>ID</th>
                    <th>Description</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                { orders.map((order) => (
                    <tr key={ order.id }>
                        <td>
                            <input value={order.id} type="checkbox" onChange={handleCheck} />
                        </td>
                        <td>{ order.id }</td>
                        <td>{ order.description }</td>
                        <td>{ order.status }</td>
                        <td>
                            <button onClick={ () => changeStatus(order.id, 'open') } className="button is-small is-danger">Open</button>
                            <button onClick={ () => changeStatus(order.id, 'preparing') } className="button is-small is-danger">Preparing</button>
                            <button onClick={ () => changeStatus(order.id, 'served') } className="button is-small is-danger">Served</button>
                            <button onClick={ () => deleteOrder(order.id) } className="button is-small is-danger">Delete</button>
                            <button>
                                <Link to={`/edit/${order.id}`} className="button is-small is-info">Edit</Link>
                            </button>
                        </td>
                    </tr>
                )) }

                </tbody>
            </table>
            <button onClick={ () => changeStatusses(checked, 'open') } className="button is-small is-danger">Open</button>
            <button onClick={ () => changeStatusses(checked, 'preparing') } className="button is-small is-danger">Preparing</button>
            <button onClick={ () => changeStatusses(checked, 'served') } className="button is-small is-danger">Served</button>
            <button onClick={ () => deleteOrders(checked) } className="button is-small is-danger">Delete</button>
        </div>
    )
}

export default OrderList
